import {useState,useEffect} from 'react';
import DisplayData from './Components/DiaplayData';
import NewsTicker, { Directions } from "./react-advanced-news-ticker"; 
import * as XLSX from 'xlsx';
import "./index.css";

function App() { 
  const [excelFile, setExcelFile]=useState(null);
  const [excelFileError, setExcelFileError]=useState(null);  

  // submits
  const [excelData, setExcelData]=useState([]);
  // it will contain array of objects

  const handleFile = (e)=>{
    let selectedFile = e.target.files[0];

    if(selectedFile){
        let reader = new FileReader();
        reader.readAsArrayBuffer(selectedFile);
        reader.onload=(e)=>{
          setExcelFileError(null);
          setExcelFile(e.target.result);            
        } 
    }
    else{
      console.log('plz select your file');
    }
  }

  // submit function
  const handleSubmit=(e)=>{
    e.preventDefault();  
    if(excelFile!=null){
      const workbook = XLSX.read(excelFile,{type:'buffer', cellDates: true });
      const worksheetName = workbook.SheetNames[0];
      const worksheet=workbook.Sheets[worksheetName];
      let data = XLSX.utils.sheet_to_json(worksheet);
      var filteredData = data.reduce((groupedUsers, user) =>{   
        var problems = {};
        var element = {};
      if(groupedUsers[user['username']] == null){ 
          groupedUsers[user['username']] = [];

          element.username = user.username;
          element.fullname = user.fullname;
          element.gender = user.gender;
          element.country_code = user.country_code;
          element.country_name = user.country_name;
          groupedUsers[user['username']].push(element);
      }

      problems.problem_code = user.problem_code;
      problems.submission_date = user.submission_date;
      problems.result_code = user.result_code;
      problems.submission_id = user.submission_id;
      groupedUsers[user['username']].push(problems);
      return groupedUsers;
      },{});

      Object.entries(filteredData).forEach(element => {
         var c=0;
        for(var i=1; i<element[1].length; i++)
        {

          if(element[1][i].result_code == 15)
             c += element[1][i].result_code;

        } element[1].total_score = c;
      });
    //  var finalData = calctotalScore(filteredData);
     // console.log(filteredData);
      setExcelData(filteredData);
    }
    else{
      setExcelData(null);
    }
  }

  return (
    <div className="container">
      {/* upload file section */}
      <div className='form'>
      <form className='form-group' autoComplete="off"
        onSubmit={handleSubmit}>
          <label><h5>Upload Excel file</h5></label>
          <br></br>
          <input type='file' className='form-control'
          onChange={handleFile} required></input>                  
          {excelFileError&&<div className='text-danger'
          style={{marginTop:5+'px'}}>{excelFileError}</div>}
          <button type='submit' className='btn btn-success'
          style={{marginTop:5+'px'}}>Submit</button>
        </form>
      </div>

      <br></br>
      <hr></hr>

      {/* view file section */}
      <h5>View Excel file</h5>
      <div className='viewer'>
        <DisplayData excelData={excelData}/>
      </div>
    </div>
  );
} 

export default App;

